	public function getPerResultStatus($result){
		$result = str_replace( array('{','}','"',':'),array( '','','',','), $result );
		$rs = explode(",", $result);
		if($rs[10]){
			return $rs[10];
		}else{
			return "";
		}
	}	
	public function getPerEmail($result){
		$result = str_replace( array('"',':'),array( '',','), $result );
		$rs = explode(",", $result);
		if($rs[8]){
			return $rs[8];
		}
		else{
			return "";
		}
	}
