    	$ajaxprice = $this->getSetting('ajaxprice');

    	$displaykeywordsuggestion = ($this->getSetting('display_keyword_suggestion'))?'true':'false';
    	$conf = array(
    		"id:'autocomplete'",
    		"target:'#{$containerid}'",
    		"inputid:'{$elementId}'",
    		"containerid:'{$containerid}'",
    		"boxWidth:469",
    		"searchTextPlaceHolder:'".$this->__('Search entire store here...')."'",
